import './App.css';
import Header from './View/Components/Header';
import Footer from './View/Components/Footer';
import RegistirationPage from './View/Pages/RegistirationPage';
import MainPage from './View/Pages/MainPage';
import UniversityPage from './View/Pages/UniversityPage';
import AccountPage from './View/Pages/AccuntPage';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { AuthContext } from './Helpers/AuthContext';
import { useState, useEffect } from 'react';

function App() {
  const [authState, setAuthState] = useState({username:"",id:0,status:false});

  useEffect(() => {
    if (localStorage.getItem("accessToken")) {
      setAuthState(true);
    }
  }, []);

  return (
    <AuthContext.Provider value={{ authState, setAuthState }}>
      <Router>
        <div className="App">
          <header className="App-header">
            <Header />
          </header>

          <div className="body-content">
            <Switch>
              <Route exact path='/'>
                <MainPage />
              </Route>
              <Route path='/Registiration'>
                <RegistirationPage />
              </Route>
              <Route path='/UniversityPage/:id'>
                <UniversityPage />
              </Route>
              <Route path='/AccountPage'>
                <AccountPage />
              </Route>
            </Switch>
          </div>

          <footer>
            <Footer />
          </footer>
        </div>
      </Router>
    </AuthContext.Provider>
  );
}

export default App;
