import SRateCSS from './SingleRate.module.css';
import { useEffect, useState } from 'react';
import axios from 'axios';

function SingleRate (props){
    const rate = props.rate;
    const [studentName, setStudentName] = useState("");

    useEffect(() => {
        // Assuming rate.stu_id exists and contains the student ID
        if (rate.stu_id) {
            axios.get(`http://localhost:3001/bystuId/${rate.stu_id}`)
                .then(response => {
                    const studentName = response.data.stu_name;
                    console.log("Student Name:", studentName);
                    // You can use studentName as needed (e.g., set it to state)
                })
                .catch(error => {
                    console.error("Error fetching student name:", error);
                });
        }
    }, [rate.stu_id]);
    
    return(
        <div class={SRateCSS.RateBody}>
            {/* Yorumcu İsmi(anonim mi?), beğen beğenme (satır sonu)
            Yıldızlar (2grid)
            Yorum */}
            <div class={SRateCSS.FirstRow}> 
                <p> {rate.com_id} </p>
                <div class={SRateCSS.LikeDislike}>
                    <i class="fa fa-thumbs-up" aria-hidden="true"></i>
                    <i class="fa fa-thumbs-down" aria-hidden="true"></i>
                </div>

            </div> 

            <div> STARS </div>
            <div> {rate.com}</div>
            <div>
                
            </div>
        </div>
    )
}

export default SingleRate;
