import SRateCSS from './SingleRate.module.css';
import { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../Helpers/AuthContext';  

function SingleRate (props){
    const rate = props.rate;
    const [studentName, setStudentName] = useState("");
    const [likeCount, setLikeCount] = useState(1);
    const { authState, setAuthSatate } = useContext(AuthContext);
    
    useEffect(() => {
        axios.get(`http://localhost:3001/rates/bystuId/${rate.stu_id}`, {
            headers: {
                accessToken: localStorage.getItem("accessToken")
            }
        })
        .then(response => {
            setStudentName(response.data.stu_name + " "+ response.data.stu_surname);
            setLikeCount(response.data.rate.like_dislike)

        })
        .catch(error => {
            console.error("Error fetching student name:", error);
        });
    }, []);
    

    const handleLike = () => {
        axios.post(`http://localhost:3001/rates/like/${rate.com_id}`,
         {com_id: rate.com_id},
         { headers: { accessToken: localStorage.getItem("accessToken") }
        }).then(response => {
            alert(response.data)
        })
    };
    
    const handleDislike = () => {

    };

    return(
        <div class={SRateCSS.RateBody}>
            {/* Yorumcu İsmi(anonim mi?), beğen beğenme (satır sonu)
            Yıldızlar (2grid)
            Yorum */}
            <div class={SRateCSS.FirstRow}> 
                <p> {studentName} </p>
                
                <div class={SRateCSS.LikeDislike}>
                    <span>{likeCount}</span>
                    <button onClick={handleLike}> <i class="fa fa-thumbs-up" aria-hidden="true"></i> </button>
                    <button onClick={handleDislike}>  <i class="fa fa-thumbs-down" aria-hidden="true"></i> </button>
                   
                </div>
            </div> 

            <div> STARS </div>
            <div> {rate.com}</div>
            <div>
            </div>
        </div>
    )
}

export default SingleRate;
