import UniPCSS from './UniversityPage.module.css'
import Rate from '../Components/Rate';
import {useParams} from 'react-router-dom';
import {useEffect} from 'react';
import axios from 'axios';

function UniversityPage(){
    let { id } = useParams();
    
    useEffect(() => {
        async function fetchUniversities() {
            try {
                const response = await axios.get(`http://localhost:3001/universities/byId${id}`);
              
                const uniqueCities = [...new Set(response.data.map(university => university.uni_province))].sort();
                const cityOptions = uniqueCities.map(city => ({
                    value: city,
                    label: city,
                }));
                setCities(cityOptions);
            } catch (error) {
                console.error('Error fetching universities:', error);
            }
        }

        fetchUniversities();
    }, []);

    return(
        <div class ={UniPCSS.UniPageBody}>
            <div class={UniPCSS.UniDetailsContainer}>
                <h1 class={UniPCSS.UniTitle}> {id} </h1>
                <p class={UniPCSS.Description}>University desciption</p>
                {/*properties*/}
                <div> <p>Email: </p>  </div>
                <div> <p>Telefon Numarası: </p></div>
                <div> <p>Şehir:  </p></div>
                <div> <p>Rektör: </p></div>
                <div> <p>Sıralama </p></div>
                <div> RATE</div>
            </div>

            <div class={UniPCSS.AddComment}>
                {/* <p><Rate/></p> */}
                <form class={UniPCSS.FormGroup}>
                    <h3>Yorum Ekle</h3>
                    <div class={UniPCSS.Rate}> <Rate/> </div>
					<input type="text" class={UniPCSS.CommentField} placeholder="Yorum" required/>
                    <button class="btn" type="submit">
                        <span></span>
                        <span></span>
                        <span></span>
                        <span></span>
                        Submit
                    </button>
				</form>	
            </div>
            <div class = {UniPCSS.RateList}>
                <div>1</div>
                <div>1</div>
                <div>1</div>
                <div>1</div>
                <div>1</div>
                <div>1</div>

            </div>
        </div>
    );
}

export default UniversityPage;
