import './SingleUniversity.css';
import University from '../../Helpers/University';

function SingleUniversity(props) {
    const university = props.university;

    function toCapitalCase(text) {
        return text
            .toLowerCase()
            .split(' ')
            .map(word => word.charAt(0).toUpperCase() + word.slice(1))
            .join(' ');
    }

    // Calculate average rating
    const averageRating = university.uni_rate / university.uni_rate_count;

    // Calculate number of full stars, half stars, and empty stars
    const fullStars = Math.floor(averageRating);
    const hasHalfStar = averageRating % 1 >= 0.3 && averageRating % 1 <= 0.7;

    // Array to store star elements
    const stars = [];

    // Fill array with full stars
    for (let i = 0; i < fullStars; i++) {
        stars.push(<span key={i} className="fa fa-star checked"></span>);
    }

    // Add half star if applicable
    if (hasHalfStar) {
        stars.push(<span key="half" className="fa fa-star-half-alt checked"></span>);
    }

    // Fill remaining array with empty stars
    for (let i = stars.length; i < 5; i++) {
        stars.push(<span key={i} className="fa fa-star"></span>);
    }

    return (
        <div className="container">
            <div className="upper">
            <iframe src="https://drive.google.com/file/d/10cvkZ41YuDiyjXq1__3HK_VOLNqqfSpQ/preview"></iframe>
            </div>
            <div className="lower">
                <div>
                    <p className="uni-title">{university.uni_name}</p>
                </div>

                <div className="rate">
                    {stars}
                </div>
                <div className="uni-property">
                    <p>
                        Sıralama = {university.rank}<br />
                        Şehir = {toCapitalCase(university.uni_province)}
                    </p>
                </div>
            </div>
        </div>
    );
}

export default SingleUniversity;
