import SRateCSS from './SingleRate.module.css';
import { useEffect } from 'react';
import axios from 'axios';

function SingleRate (props){
    const rate = props.rate;

    useEffect(() => {
        axios.get("http://localhost:3001/universities")
            .then(response => {
                const universityOptions = response.data.map(university => ({
                    value: university.uni_id,
                    label: university.uni_name
                }));
                setUniversities(universityOptions);
            })
            .catch(error => {
                console.error("There was an error fetching the universities!", error);
            });
    }, []);
    return(
        <div class={SRateCSS.RateBody}>
            {/* Yorumcu İsmi(anonim mi?), beğen beğenme (satır sonu)
            Yıldızlar (2grid)
            Yorum */}
            <div class={SRateCSS.FirstRow}> 
                <p> {rate.com_id} </p>
                <div class={SRateCSS.LikeDislike}>
                    <i class="fa fa-thumbs-up" aria-hidden="true"></i>
                    <i class="fa fa-thumbs-down" aria-hidden="true"></i>
                </div>

            </div> 

            <div> STARS </div>
            <div> {rate.com}</div>
            <div>
                
            </div>
        </div>
    )
}

export default SingleRate;
