import { useEffect, useState } from 'react';
import AccCSS from './AccountPage.module.css';
import axios from 'axios';

function AccountPage() {
    const [student, setStudent] = useState(null);

    useEffect(() => {
        axios.get("http://localhost:3001/students/account", {
            headers: {
                accessToken: localStorage.getItem("accessToken"), // Ensure the token is included in the request
            }
        }).then(response => {
            setStudent(response.data.student);
            console.log(response.data.student);
        }).catch(error => {
            console.error('There was an error fetching the student data:', error);
        });
    }, []);

    function convertDriveLink(link) {
        if (link) { // Check if link is not null
            const fileId = link.split('/d/')[1].split('/')[0];
            return `https://drive.google.com/thumbnail?id=${fileId}`;
        } else {
            return 'https://drive.google.com/thumbnail?id=1kID4I0OyUGhvGdXt3dxUKTt16HL2W0v1';
        }
    }

    return (
        <div className={AccCSS.AccountPageBody}>
            {student ? (
                <>
                    <div className={AccCSS.AccountInfo}></div>
          
                    <img
                        className={AccCSS.UniLogo}
                        src={convertDriveLink(student.uni_logo)}
                        alt="school_logo"
                    />
             
                    <div className={AccCSS.AccountName}>
                        <input type="text" disabled placeholder={student.name} />
                        <input type="text" disabled placeholder={student.surname} />
                    </div>

                    <div className={AccCSS.AccountEmail}>
                        <p>{student.university}</p>
                        <input type="email" disabled placeholder={student.email} />
                    </div>

                    <div>
                        <button className={AccCSS.SubmitBtn} >Çıkış Yap</button>
                    </div>

                    <div className={AccCSS.Empty}></div>
                </>
            ) : (
                <p class>Yükleniyor...</p>
            )}
        </div>
    );
}

export default AccountPage;
